import React, { useState } from 'react';
import axios from 'axios'; // make sure to install axios with npm or yarn
import './LoginSignup.css';

// importing the assets
import user_icon from '../Assets/person.png';
import email_icon from '../Assets/email.png';
import password_icon from '../Assets/password.png';

const LoginSignup = () => {
  const [action, setAction] = useState("Sign Up");
  const [userData, setUserData] = useState({
    name: '',
    email: '',
    password: ''
  });

  // Update state with user input
  const handleChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };

  // Handle user registration or login
  const handleSubmit = async () => {
    const endpoint = action === "Sign Up" ? 'register' : 'login';
    try {
      const response = await axios.post(`http://localhost:5002/${endpoint}`, userData);
      console.log(response.data);
      // Redirect or perform actions after success
    } catch (error) {
      console.error('There was an error!', error);
    }
  };

  // Toggle between 'Sign Up' and 'Login'
  const toggleAction = () => {
    setAction(prevAction => prevAction === "Sign Up" ? "Login" : "Sign Up");
  };

  return (
    <div className='container'>
      <div className='header'>
        <div className='text'> {action} </div>
      </div>
      <div className='inputs'>
        {action === "Sign Up" && (
          <div className='input'>
            <img src={user_icon} alt='' />
            <input 
              type="text" 
              placeholder='Name' 
              name="name"
              value={userData.name}
              onChange={handleChange}
            />
          </div>
        )}

        <div className='input'>
          <img src={email_icon} alt='' />
          <input 
            type="email" 
            placeholder='Email' 
            name="email"
            value={userData.email}
            onChange={handleChange}
          />
        </div>
        <div className='input'>
          <img src={password_icon} alt='' />
          <input 
            type="password" 
            placeholder='Password' 
            name="password"
            value={userData.password}
            onChange={handleChange}
          />
        </div>
      </div>

      {action === "Sign Up" && (
        <div className='forgot-password'> Lost Password? <span>Click here</span></div>
      )}

      <div className='submit-container'>
        <div 
          className={action === "Login" ? "submit gray" : "submit"} 
          onClick={handleSubmit}
        > 
          {action}
        </div>
        <div 
          className={action === "Sign Up" ? "submit gray" : "submit"} 
          onClick={toggleAction}
        > 
          {action === "Sign Up" ? "Login" : "Sign Up"}
        </div>
      </div>
    </div>
  );
}

export default LoginSignup;
