import React, { useState } from 'react';
import './LoginSignup.css';
import user_icon from '../Assets/person.png';
import email_icon from '../Assets/email.png';
import password_icon from '../Assets/password.png';

const LoginSignup = () => {
  const [action, setAction] = useState("Sign Up");
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
    // Function to switch to the sign-up view
    const switchToSignUp = () => {
      setAction("Sign Up");
      setMessage('');
      // Reset the fields when switching to the sign-up view
      setName('');
      setEmail('');
      setPassword('');
    };

    // Function to switch to the login view
    const switchToLogin = () => {
      setAction("Login");
      setMessage('');
    };

  // Function to handle the sign up action
  const handleSignUp = () => {
    const userData = {
      name: name,
      email: email,
      password: password,
    };

    fetch('http://localhost:5002/register', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(userData),
    })
    .then(response => response.json())
    .then(data => {
      // Handle response data
      console.log(data);
    })
    .catch(error => {
      // Handle error
      console.error(error);
    });
  };

  return (
    <div className='container'>
      <div className='header'>
        <div className='text'>{action}</div>
      </div>
      <div className='inputs'>
        {action === "Sign Up" && (
          <div className='input'>
            <img src={user_icon} alt='' />
            <input type="text" placeholder='Name' value={name} onChange={(e) => setName(e.target.value)} />
          </div>
        )}
        <div className='input'>
          <img src={email_icon} alt='' />
          <input type="email" placeholder='Email' value={email} onChange={(e) => setEmail(e.target.value)} />
        </div>
        <div className='input'>
          <img src={password_icon} alt='' />
          <input type="password" placeholder='Password' value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
      </div>
      {action === "Sign Up" && (
        <div className='forgot-password'> Lost Password? <span>Click here</span></div>
      )}
      <div className='submit-container'>
        <div className={action === "Login" ? "submit gray" : "submit"} onClick={handleSignUp}> Sign Up</div>
        <div className={action === "Sign Up" ? "submit gray" : "submit"} onClick={() => { setAction("Login") }}> Login</div>
      </div>
    </div>
  );
};

export default LoginSignup;
